import React, { useState } from 'react';
import styles from '../styles/main.module.css';
import previewImage from '../img/carConfigurator.png';
import { ShapeType } from 'react-particles-js';

import cancelIcon from '../img/close.png';

import wordpressLogo from '../img/1024px-Wordpress-Logo.svg.png';
import reactLogo from '../img/react.png';
import contentfulLogo from '../img/contentful.png';
import figmaLogo from '../img/figma.png';
import htmlLogo from '../img/html.png';
import cssLogo from '../img/css.png';

import projectsList from '../projects.json';


class Main extends React.Component {

    constructor(props) {
        super(props)

        this.state = {

            opened: false,
            projects: []
        };






    }
    componentDidMount() {
        const projectsListVar = projectsList;
        this.setState({ opened: false, projects: projectsListVar });
    }

    trigger = () => {
        this.setState({ opened: !this.state.opened });
    }



    render() {
        return (
            < div className={styles.main} >
                <h1 className={styles.componentHeadline}>Projects</h1>
                <div className={styles.carousel}>

                    {/*Container */}

                    {this.state.projects.projects && this.state.projects.projects.map(project => {
                        return (
                            <div className={styles.container} onClick={this.trigger}>
                                <img src={previewImage}></img>
                                <div className={styles.textPart}>
                                    <p className={styles.tag}>{project.tag}</p>
                                    <h1>{project.title}</h1>
                                    <p className={styles.perex}>{project.perex}</p>
                                    <button>Show more</button>
                                </div>
                            </div>

                        )
                    })}

                    <div className={styles.container}>
                        <img src={previewImage}></img>
                        <div className={styles.textPart}>
                            <p className={styles.tag}>Personal project</p>
                            <h1>Car Configurator</h1>
                            <p className={styles.perex}>Lorem ipsum dolor sit amet,
                            consectetur adipiscing elit.
                            Elementum tellus volutpat aliquam et quis.
                            Ipsum eleifend mattis lacus aliquam molestie viverra velit.
                            Donec in feugiat faucibus mauris commodo neque ornare.
                     Ultricies sed orci nisi.</p>
                            <button>Show more</button>
                        </div>
                    </div>

                    <div className={styles.container}>
                        <img src={previewImage}></img>
                        <div className={styles.textPart}>
                            <p className={styles.tag}>Personal project</p>
                            <h1>Car Configurator</h1>
                            <p className={styles.perex}>Lorem ipsum dolor sit amet,
                            consectetur adipiscing elit.
                            Elementum tellus volutpat aliquam et quis.
                            Ipsum eleifend mattis lacus aliquam molestie viverra velit.
                            Donec in feugiat faucibus mauris commodo neque ornare.
                     Ultricies sed orci nisi.</p>
                            <button>Show more</button>
                        </div>
                    </div>

                    <div className={styles.container}>
                        <img src={previewImage}></img>
                        <div className={styles.textPart}>
                            <p className={styles.tag}>Personal project</p>
                            <h1>Car Configurator</h1>
                            <p className={styles.perex}>Lorem ipsum dolor sit amet,
                            consectetur adipiscing elit.
                            Elementum tellus volutpat aliquam et quis.
                            Ipsum eleifend mattis lacus aliquam molestie viverra velit.
                            Donec in feugiat faucibus mauris commodo neque ornare.
                     Ultricies sed orci nisi.</p>
                            <button>Show more</button>
                        </div>
                    </div>

                </div>

                {/*SPACER*/}
                <div className={styles.spacer}></div>



                {/*POPUP CONTAINER */}
                {
                    this.state.opened === true ?

                        <div className={styles.popupContainer}>
                            <img src={cancelIcon} className={styles.cancelButton} onClick={this.trigger}></img>
                            <div className={styles.popupData}>
                                <img src={previewImage}></img>
                                <div className={styles.innerData}>
                                    <h1>Car Configurator</h1>
                                    <p>Lorem ipsum dolor sit amet,
                                    consectetur adipiscing elit.
                                    Porttitor in at etiam velit.
                                    Facilisi quisque viverra nunc turpis pulvinar sed fames vulputate.
                                    Rutrum placerat quisque mattis massa vestibulum at.
                                    Fermentum nulla velit gravida viverra laoreet facilisis.
                                    Lorem ipsum dolor sit amet, consectetur adipiscing elit.
                                    Porttitor in at etiam velit.
                             Facilisi quisque viverra nunc turpis pulvinar sed fames</p>
                                    <img src={reactLogo}></img>
                                    <img src={htmlLogo}></img>
                                    <img src={cssLogo}></img>
                                    <img src={figmaLogo}></img>
                                    <img src={contentfulLogo}></img>
                                    <div className={styles.links}>
                                        <a href='#'> <button>Website</button></a>
                                        <a href='#'><button>GitHub</button></a>
                                    </div>
                                </div>

                            </div>

                        </div>
                        : null
                }
            </div >
        )
    }
}


export default Main;
