import React from 'react';
import styles from '../styles/main.module.css';
import Particles from 'react-particles-js';


const main = () => {

    const write = () => {
        const texts = ['Web Developer', 'Graphic Designer', 'Coder'];
        let count = 0;
        let index = 0;
        let currentText = '';
        let letter = '';

        (function type() {

            if (count === texts.length) {
                count = 0;
            }

            currentText = texts[count];
            letter = currentText.slice(0, ++index);
            document.querySelector('.selfWritingText').textContent = letter;
            if (letter.length === currentText.length) {
                count++;
                index = 0;
            }
            setTimeout(type, 4000);


        }());
    }
    write();

    return (
        <div>
            <div className={styles.particlesContainer}>
                <Particles />
                <div className={styles.particlesText}>
                    <h1>I Am <span className={styles.selfWritingText}></span></h1>
                </div>
            </div>
            <section className={styles.projectsSection}></section>
        </div>
    )
}

export default main
